import React from 'react';
import * as THREE from 'three';

interface GameState {
  player1Score: number;
  player2Score: number;
  gameStatus: 'playing' | 'paused' | 'ended';
}

interface Platform {
  mesh: THREE.Mesh;
  top: number;
  bottom: number;
  left: number;
  right: number;
}

class PlatformClass implements Platform {
  mesh: THREE.Mesh;
  top: number;
  bottom: number;
  left: number;
  right: number;

  constructor(x: number, y: number, width: number = 2) {
    const geometry = new THREE.BoxGeometry(width, 0.2, 1);
    const material = new THREE.MeshPhongMaterial({ color: 0x95a5a6 });
    this.mesh = new THREE.Mesh(geometry, material);
    this.mesh.position.set(x, y, 0);

    this.top = y + 0.1;
    this.bottom = y - 0.1;
    this.left = x - width/2;
    this.right = x + width/2;
  }
}

class SpellClass {
  mesh: THREE.Mesh;
  velocity: THREE.Vector3;
  lifetime: number;
  exploded: boolean;
  explosionRadius: number;
  explosionMesh?: THREE.Mesh;

  constructor(position: THREE.Vector3, direction: THREE.Vector3) {
    const geometry = new THREE.SphereGeometry(0.2);
    const material = new THREE.MeshBasicMaterial({ 
      color: 0xffff00,
      transparent: true,
      opacity: 0.8
    });
    this.mesh = new THREE.Mesh(geometry, material);

    const spawnOffset = new THREE.Vector3(
      direction.x * 1,
      1,
      0
    );
    this.mesh.position.copy(position).add(spawnOffset);

    this.velocity = new THREE.Vector3(
      direction.x * 0.3,
      0.3,
      0
    );

    this.lifetime = 30;
    this.exploded = false;
    this.explosionRadius = 1.5;
  }

  update(scene: THREE.Scene): boolean {
    if (this.exploded) return true;

    this.lifetime--;
    if (this.lifetime <= 0) {
      this.explode(scene);
      return true;
    }

    this.mesh.position.add(this.velocity);
    return false;
  }

  explode(scene: THREE.Scene): void {
    this.exploded = true;
    const explosionGeo = new THREE.SphereGeometry(this.explosionRadius);
    const explosionMat = new THREE.MeshBasicMaterial({
      color: 0xff8800,
      transparent: true,
      opacity: 0.5
    });
    this.explosionMesh = new THREE.Mesh(explosionGeo, explosionMat);
    this.explosionMesh.position.copy(this.mesh.position);
    scene.add(this.explosionMesh);

    setTimeout(() => {
      if (this.explosionMesh) {
        scene.remove(this.explosionMesh);
        scene.remove(this.mesh);
      }
    }, 100);
  }
}

interface Keys {
  player1: {
    left: boolean;
    right: boolean;
    jump: boolean;
    spell: boolean;
  };
  player2: {
    left: boolean;
    right: boolean;
    jump: boolean;
    spell: boolean;
  };
}

class BallClass {
  mesh: THREE.Mesh;
  velocity: THREE.Vector3;

  constructor() {
    const geometry = new THREE.SphereGeometry(0.3);
    const material = new THREE.MeshBasicMaterial({ color: 0xffffff });
    this.mesh = new THREE.Mesh(geometry, material);
    this.velocity = new THREE.Vector3(
      (Math.random() - 0.5) * 0.2,
      (Math.random() - 0.5) * 0.2,
      0
    );
    this.mesh.position.set(0, 2, 0);
  }

  update(gameHeight: number): void {
    this.mesh.position.add(this.velocity);

    // Horizontal wrapping (endless scroll)
    if (this.mesh.position.x > 10) this.mesh.position.x = -10;
    if (this.mesh.position.x < -10) this.mesh.position.x = 10;

    // Vertical bouncing
    if (this.mesh.position.y > gameHeight || this.mesh.position.y < 0) {
      this.velocity.y *= -1;
    }
  }

  bounce(normal: THREE.Vector3, speed: number = 1): void {
    const dot = this.velocity.dot(normal);
    this.velocity.sub(normal.multiplyScalar(2 * dot));
    this.velocity.multiplyScalar(speed);
  }

  reset(): void {
    this.mesh.position.set(0, 2, 0);
    this.velocity.set(
      (Math.random() - 0.5) * 0.2,
      (Math.random() - 0.5) * 0.2,
      0
    );
  }
}

class PlayerClass {
  mesh: THREE.Mesh;
  velocity: THREE.Vector3;
  onGround: boolean;
  direction: THREE.Vector3;

  constructor(color: number, startX: number) {
    const geometry = new THREE.SphereGeometry(0.5);
    const material = new THREE.MeshBasicMaterial({ color });
    this.mesh = new THREE.Mesh(geometry, material);
    this.mesh.position.set(startX, 0, 0);
    this.velocity = new THREE.Vector3(0, 0, 0);
    this.onGround = true;
    this.direction = new THREE.Vector3(1, 0, 0);
  }

  update(platforms: Platform[]): void {
    if (!this.onGround) {
      this.velocity.y -= 0.015; // Gravity
    }

    const nextPosition = this.mesh.position.clone().add(this.velocity);

    // Platform collision
    this.onGround = false;
    platforms.forEach(platform => {
      if (nextPosition.x >= platform.left && 
          nextPosition.x <= platform.right && 
          nextPosition.y >= platform.bottom &&
          nextPosition.y <= platform.top) {
        if (this.velocity.y < 0) {
          nextPosition.y = platform.top;
          this.velocity.y = 0;
          this.onGround = true;
        }
      }
    });

    this.mesh.position.copy(nextPosition);

    // Floor collision
    if (this.mesh.position.y < 0) {
      this.mesh.position.y = 0;
      this.velocity.y = 0;
      this.onGround = true;
    }

    // Horizontal wrapping
    if (this.mesh.position.x > 10) this.mesh.position.x = -10;
    if (this.mesh.position.x < -10) this.mesh.position.x = 10;

    this.velocity.x *= 0.9; // Friction
  }

  jump(): void {
    if (this.onGround) {
      this.velocity.y = 0.6; // Increased jump height
      this.onGround = false;
    }
  }

  moveLeft(): void {
    this.velocity.x = -0.15;
    this.direction.x = -1;
  }

  moveRight(): void {
    this.velocity.x = 0.15;
    this.direction.x = 1;
  }

  castSpell(scene: THREE.Scene): SpellClass {
    return new SpellClass(this.mesh.position, this.direction);
  }
}

const SpyGame: React.FC = () => {
  const mountRef = React.useRef<HTMLDivElement>(null);
  const [gameState, setGameState] = React.useState<GameState>({
    player1Score: 0,
    player2Score: 0,
    gameStatus: 'playing'
  });

  React.useEffect(() => {
    if (!mountRef.current) return;

    // Scene setup
    const scene = new THREE.Scene();
    scene.background = new THREE.Color(0x2c3e50);

    // Camera and renderer setup
    const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
    camera.position.set(0, 5, 10);

    const renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.setSize(window.innerWidth, window.innerHeight);
    mountRef.current.appendChild(renderer.domElement);

    // Controls
    const keys: Keys = {
      player1: { left: false, right: false, jump: false, spell: false },
      player2: { left: false, right: false, jump: false, spell: false }
    };

    const handleKeyDown = (e: KeyboardEvent) => {
      switch(e.key) {
        case 'a': keys.player1.left = true; break;
        case 'd': keys.player1.right = true; break;
        case 'w': keys.player1.jump = true; break;
        case 'e': keys.player1.spell = true; break;
        case 'ArrowLeft': keys.player2.left = true; break;
        case 'ArrowRight': keys.player2.right = true; break;
        case 'ArrowUp': keys.player2.jump = true; break;
        case 'p': keys.player2.spell = true; break;
      }
    };

    const handleKeyUp = (e: KeyboardEvent) => {
      switch(e.key) {
        case 'a': keys.player1.left = false; break;
        case 'd': keys.player1.right = false; break;
        case 'w': keys.player1.jump = false; break;
        case 'e': keys.player1.spell = false; break;
        case 'ArrowLeft': keys.player2.left = false; break;
        case 'ArrowRight': keys.player2.right = false; break;
        case 'ArrowUp': keys.player2.jump = false; break;
        case 'p': keys.player2.spell = false; break;
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    window.addEventListener('keyup', handleKeyUp);

    // Cleanup function
    return () => {
      mountRef.current?.removeChild(renderer.domElement);
      window.removeEventListener('keydown', handleKeyDown);
      window.removeEventListener('keyup', handleKeyUp);
    };
  }, []);

  return (
    <div className="w-full h-screen">
      <div ref={mountRef} className="w-full h-full" />
      <div className="absolute top-4 left-4 bg-black/50 text-white p-4 rounded">
        <div>Red Score: {gameState.player1Score}</div>
        <div>Blue Score: {gameState.player2Score}</div>
      </div>
    </div>
  );
};

export default SpyGame;
